ElectCalc=function(t){this.teachers=parseInt(t),this.maxWorkers=Math.floor(.1*t/.75),this.minStudents=Math.ceil(.2*t)},$(document).ready(function(){$("#elect_calc, .elect-calc").click(function(){var t=new ElectCalc($('[name="elect_teacher"]').val());$("#quota-teachers").text(t.teachers),$("#quota-workers").text(t.maxWorkers),$("#quota-students").text(t.minStudents),$("#quota-total").text(t.teachers+t.maxWorkers+t.minStudents),$(".elect-quotas-wrap").slideDown()})});